import Courselist from './component/list'



class App extends Component{

state={
  courses:[
    {name:'html'},
    {name:'css'},
    {name:'jquery'},
    {name:'react'}



  ],
  curent:""
}
updateCourse=(e)=>{
this.setState({
  curent:e.target.value
})
}
addcourse=(e)=>{
  e.preventDefault()
let curent=this.state.curent
let courses=this.state.courses
courses.push({
  name:curent
})
}

 render() {
   const {courses}=this.state

   const courselist=courses.map((course,index)=>{


     return  < Courselist  details={course} key={index}/>


   })
  return (
    <section className="App">
    <h2> add course</h2>
<Courseform update={this.updateCourse} addcourse={this.addcourse}/>
<ul>{courselist}</ul>
    </section>
  );
}
}

export default App;
