import React,{Component,Fragment} from 'react'

class Courselist extends Component{

state={
  edited:false
}

  renderCourse=()=>{
    return(
    <li>
    <span>{this.props.details.name}</span>
        <button onClick={()=>this.switch()} >Edit course</button>
    <button onClick={()=>this.props.deletecourse(this.props.index)}>delete course</button>

    </li>
  )
  }
  switch=()=>{
    let {edited}=this.state
    this.setState({
      edited:!edited
    })
  }
  updatingCourse=()=>{
    return(
      <form>
      <input type="text" defaultValue={this.props.details.name}/>
      <button onClick={this.props.updateCourse(this.props.index)}>Update COURSE</button>
      </form>
    )
  }


  render(){
let {edited}=this.state
return(


<Fragment>
{ edited? this.updatingCourse():this.renderCourse()}
</Fragment>


)


  }
}
export default Courselist
