import React,{Component,Fragment} from 'react'

class Courselist extends Component{

state={
  edited:false
}

  renderCourse=()=>{
    return(
    <li className="listStyle">
    <span>{this.props.details.name}</span>
        <button onClick={()=>this.switch()} >Edit course</button>
    <button onClick={()=>this.props.deletecourse(this.props.index)}>delete course</button>

    </li>
  )
  }
  newItem=(e)=>{
    e.preventDefault()
    this.props.updateCourse(this.props.index,this.input.value)
    this.switch()

  }
  switch=()=>{
    let {edited}=this.state
    this.setState({
      edited:!edited
    })
  }
  updatingCourse=()=>{
    return(
      <form className="form2" onSubmit={this.newItem}>
      <input type="text"  ref={(v)=>{this.input=v}} defaultValue={this.props.details.name}/>
      <button>Update COURSE</button>
      </form>
    )
  }


  render(){
let {edited}=this.state
return(


<Fragment>
{ edited? this.updatingCourse():this.renderCourse()}
</Fragment>


)


  }
}
export default Courselist
